                " LEFT JOIN Sectors ON MovementInvoices.RecipientSectorID = Sectors.SectorID" +
                " LEFT JOIN infiniu2_users.dbo.Users ON MovementInvoices.StoreKeeperID = infiniu2_users.dbo.Users.UserID",
