            for (int i = MovementInvoicesDT.Rows.Count - 1; i >= 0; i--)
            {
                int MovementInvoiceID = Convert.ToInt32(MovementInvoicesDT.Rows[i]["MovementInvoiceID"]);

                //bool b = FilterByMovementInvoice(MovementInvoiceID, FactoryID, 2);
                //if (!b)
