                " INNER JOIN infiniu2_users.dbo.Users ON MovementInvoices.StoreKeeperID = infiniu2_users.dbo.Users.UserID" + Filter,
                ConnectionStrings.StorageConnectionString))
            {
                InvoicesDataTable.Clear();
