                    }

                    DataRow[] StoreRows = StoreDT.Select("ReadyStoreID = " + StoreIDFrom);
                    if (StoreRows.Count() > 0)
                    {
                        StoreRows[0]["SellingCount"] = SellingCount;
                    }
                }
            }
            MovementInvoiceDetailsDT.Dispose();
            Table.Dispose();
            sw.Stop();
            double G = sw.Elapsed.TotalSeconds;
        }

