                    DA.Fill(DT);

                    DataRow EmptyRow = CoversDT.NewRow();
                    EmptyRow["CoverID"] = -1;
                    EmptyRow["CoverName"] = "-";
                    CoversDT.Rows.Add(EmptyRow);

                    DataRow ChoiceRow = CoversDT.NewRow();
                    ChoiceRow["CoverID"] = 0;
                    ChoiceRow["CoverName"] = "на выбор";
                    CoversDT.Rows.Add(ChoiceRow);

                    for (int i = 0; i < DT.Rows.Count; i++)
